CREATE TABLE "public"."applications" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "name" text NOT NULL, "email_address" text NOT NULL, "discord_handle" text NOT NULL, "telegram_handle" text NOT NULL, "twitter_handle" text NOT NULL, "github_handle" text NOT NULL, "eth_address" text NOT NULL, "ens_name" text NOT NULL, "introduction" text NOT NULL, "skill_type" text NOT NULL, "passion" text NOT NULL, "favorite_media" text NOT NULL, "crypto_thrills" text NOT NULL, "why_raidguild" text NOT NULL, "dao_familiarity" text NOT NULL, "availability" text NOT NULL, "crypto_experience" text NOT NULL, "comments" text NOT NULL, "handbook_read" boolean NOT NULL, "pledge_readiness" boolean NOT NULL, "referred_by" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("skill_type") REFERENCES "public"."skill_types"("skill_type") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("dao_familiarity") REFERENCES "public"."dao_familiarities"("dao_familiarity") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("availability") REFERENCES "public"."cohort_availabilities"("cohort_availability") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("referred_by") REFERENCES "public"."members"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
